import 'package:flutter/material.dart';import 'package:qr_code_scanner_app/screens/qr_create_page.dart';import 'package:qr_code_scanner_app/screens/qr_scan_page.dart';class MyHomePage extends StatefulWidget {  const MyHomePage({Key? key}) : super(key: key);  @override  State<MyHomePage> createState() => _MyHomePageState();}class _MyHomePageState extends State<MyHomePage> {  int _currentIndex = 0;  void _onItemTapped(int index) {    setState(() {      _currentIndex = index;    });  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: _currentIndex == 0 ? const QrCreatePage() : const QrScanPage(),      bottomNavigationBar: BottomNavigationBar(        selectedIconTheme: const IconThemeData(color: Colors.cyanAccent),        // unselectedItemColor: Theme.of(context).accentColor,        elevation: 4,        selectedItemColor: Theme.of(context).colorScheme.secondary,        iconSize: 30,        backgroundColor: const Color.fromRGBO(39, 48, 56, 1),        currentIndex: _currentIndex,        onTap: _onItemTapped,        items: const [          BottomNavigationBarItem(            icon: Icon(              Icons.add_outlined,              //   color: Colors.teal,            ),            label: "Create Qr",          ),          BottomNavigationBarItem(            label: "Scan Qr",            icon: Icon(              Icons.qr_code_scanner_outlined,              //    color: Colors.teal,            ),          ),        ],      ),    );  }}